import { React, useState } from "react";
import Card from '@mui/material/Card';
import List from '@mui/material/List';
import Chip from '@mui/material/Chip';
import Skeleton from '@mui/material/Skeleton';
import Box from '@mui/material/Box';


function ProjectCard(props) {
    let linkElement;

    if (props.link == "") {
        linkElement = <p>Public repo coming soon</p>
    } else {
        linkElement = <a href={props.link}>{props.link_description}</a>
    }

    const [imageLoaded, setImageLoaded] = useState(false);

    return (
        <Card variant="outlined" className="project-card">
            <h1>{props.project_name}</h1>
            <p>{props.description}</p>

            <Box>
            {imageLoaded ? (<img className="project-card-img" src={props.img} alt={props.description} onLoad={() => setImageLoaded(true)} />) : (<Skeleton variant="rectangular" width={210} height={118} />)}
            </Box>


            <List>
                {props.technologies.map((technology, index) => (
                    <Chip label={technology} variant="outlined" />
                ))}
            </List>

            {linkElement}

        </Card>
    )
}

export default ProjectCard
